import java.util.Scanner;
public class StringArrayConcatenation {
public static void main(String[] args) {
Scanner scanner = new Scanner(System.in);
System.out.print("Enter the number of strings in the array: ");
int size = scanner.nextInt();
scanner.nextLine();
String[] strings = new String[size];
System.out.println("Enter " + size + " strings:");
for (int i = 0; i < size; i++) {
System.out.print("String " + (i + 1) + ": ");
strings[i] = scanner.nextLine();
}
StringBuilder concatenatedString = new StringBuilder();
for (String str : strings) {
 concatenatedString.append(str);
}
System.out.println("The concatenated string is: " + concatenatedString.toString());
scanner.close();
}
}

import java.util.Scanner;

public class FactorialCalculator {
  public static void main(String[] args) {
    Scanner scanner = new Scanner(System.in);

    System.out.print("Enter a number to find its factorial: ");
    int num = scanner.nextInt();
    long factorial = 1;

    for (int i = 1; i <= num; i++) {
      factorial *= i;
    }

    System.out.println("Factorial of " + num + " is: " + factorial);
    scanner.close();
  }
}

import java.util.Scanner;

public class PalindromeChecker {
  public static void main(String[] args) {
    Scanner scanner = new Scanner(System.in);

    System.out.print("Enter a string to check for palindrome: ");
    String input = scanner.nextLine();

    String reversed = new StringBuilder(input).reverse().toString();

    if (input.equalsIgnoreCase(reversed)) {
      System.out.println(input + " is a palindrome.");
    } else {
      System.out.println(input + " is not a palindrome.");
    }

    scanner.close();
  }
}

